mapper.select:
SELECT
  *
FROM c01_goods
WHERE delete_flag=1

    AND name LIKE CONCAT('%',#{shopGoodsPageForm.goodsName},'%')


    AND UNIX_TIMESTAMP(create_time) >= UNIX_TIMESTAMP(#{shopGoodsPageForm.startTime})


    AND UNIX_TIMESTAMP(create_time) <= UNIX_TIMESTAMP(#{shopGoodsPageForm.endTime})


    AND examine_status = #{shopGoodsPageForm.goodsStatus}


    AND shop_id = #{shopGoodsPageForm.shopId}


    AND type != #{shopGoodsPageForm.type}


    AND type = #{shopGoodsPageForm.types}

ORDER BY create_time desc,comprehensive_sort,evaluate_sort

SELECT * FROM c01_goods WHERE delete_flag=1
AND name LIKE CONCAT('%',#{shopGoodsPageForm.goodsName},'%')
AND UNIX_TIMESTAMP(create_time) >= UNIX_TIMESTAMP(#{shopGoodsPageForm.startTime})
AND UNIX_TIMESTAMP(create_time) <= UNIX_TIMESTAMP(#{shopGoodsPageForm.endTime})
AND examine_status = #{shopGoodsPageForm.goodsStatus}
AND shop_id = #{shopGoodsPageForm.shopId}
AND type = #{shopGoodsPageForm.type}
ORDER BY id desc

SELECT

FROM c01_goods 

     and category_id = #{categoryId} 
     and reason = #{reason} 
     and second_category_id = #{secondCategoryId} 
     and third_category_id = #{thirdCategoryId} 
     and name = #{name} 
     and price = #{price} 
     and stock = #{stock} 
     and goods_desc = #{goodsDesc} 
     and sale_num = #{saleNum} 
     and is_sale = #{isSale} 
     and examine_status = #{examineStatus} 
     and shop_id = #{shopId} 
     and shop_name = #{shopName} 
     and icon = #{icon} 
     and browse_volume = #{browseVolume} 
     and create_person = #{createPerson} 
     and create_time = #{createTime} 
     and update_person = #{updatePerson} 
     and update_time = #{updateTime} 
     and comprehensive_sort = #{comprehensiveSort} 
     and evaluate_sort = #{evaluateSort} 
     and goods_no = #{goodsNo} 
     and collection_volume = #{collectionVolume} 
     and type = #{type} 
     and introduction = #{introduction} 
     and delete_flag = #{deleteFlag} 



        order by #{sort} #{order}


        order by id desc



    limit #{offset}, #{limit}

