SELECT
 c.*, count(d.id) as evaluateCount
FROM c01_goods c
left join d04_goods_evaluate d on d.goods_id=c.id

     and c.is_sale = #{isSale} 
     and c.shop_id = #{shopId} 
     and c.delete_flag = #{deleteFlag} 

 group by c.id
 order by c.id desc

SELECT
c.*, count(d.id) as evaluateCount
FROM c01_goods c
left join d04_goods_evaluate d on d.goods_id=c.id

     and c.is_sale = #{isSale} 
     and c.shop_id = #{shopId} 
     and c.delete_flag = #{deleteFlag} 

group by c.id
order by c.id desc

SELECT
c.*,f.province,ca.`name` as city,f.area
FROM c01_goods c
left Join f01_shop f on c.shop_id=f.id
left JOIN c03_category ca on ca.id = c.third_category_id

and c.is_sale =2 and c.delete_flag = 1
     and c.category_id = #{categoryId} 
     and c.second_category_id = #{secondCategoryId} 
     and c.reason = #{reason} 
     and c.third_category_id = #{thirdCategoryId} 
     and c.type = #{type} 
     and c.name like CONCAT('%',#{name},'%') 
     and c.price = #{price} 
     and c.stock = #{stock} 
     and c.goods_desc = #{goodsDesc} 
     and c.sale_num = #{saleNum} 
     and c.is_sale = #{isSale} 
     and c.examine_status = #{examineStatus} 
     and c.shop_id = #{shopId} 
     and c.shop_name = #{shopName} 
     and c.icon = #{icon} 
     and c.browse_volume = #{browseVolume} 
     and c.create_person = #{createPerson} 
     and c.create_time = #{createTime} 
     and c.update_person = #{updatePerson} 
     and c.update_time = #{updateTime} 
     and c.evaluate_sort = #{evaluateSort} 
     and c.goods_no = #{goodsNo} 
     and c.collection_volume = #{collectionVolume} 
     and c.introduction = #{introduction} 
     and c.delete_flag = #{deleteFlag} 



        order by c.comprehensive_sort ${orderType}


        order by c.sale_num  ${orderType}


        order by c.price  ${orderType}


        order by c.evaluate_sort  ${orderType}


        order by c.id desc

