mapper.select:
SELECT

FROM c04_goods_examine_set 

             and examine_flag = #{examineFlag} 
 and content = #{content} 
 and create_person = #{createPerson} 
 and create_time = #{createTime} 
 and update_person = #{updatePerson} 
 and update_time = #{updateTime} 
 and delete_flag = #{deleteFlag} 



        order by #{sort} #{order}


        order by id desc



    limit #{offset}, #{limit}

select count(*) from c04_goods_examine_set

             and examine_flag = #{examineFlag} 
 and content = #{content} 
 and create_person = #{createPerson} 
 and create_time = #{createTime} 
 and update_person = #{updatePerson} 
 and update_time = #{updateTime} 
 and delete_flag = #{deleteFlag}

update.foreach:
UPDATE c04_goods_examine_set
examine_flag = #{item.examineFlag}, content = #{item.content}, create_person = #{item.createPerson}, create_time = #{item.createTime}, update_person = #{item.updatePerson}, update_time = #{item.updateTime}, delete_flag = #{item.deleteFlag},
id = #{item.id}
