mapper.select:
SELECT

FROM d06_pay_order 

             and order_no = #{orderNo} 
 and user_id = #{userId} 
 and nick = #{nick} 
 and user_phone = #{userPhone} 
 and order_price = #{orderPrice} 
 and pay_type = #{payType} 
 and trade_no = #{tradeNo} 
 and order_status = #{orderStatus} 
 and buy_msg = #{buyMsg} 
 and shipping_name = #{shippingName} 
 and shipping_phone = #{shippingPhone} 
 and province = #{province} 
 and city = #{city} 
 and area = #{area} 
 and invoice_flag = #{invoiceFlag} 
 and invoice_type = #{invoiceType} 
 and invoice_content = #{invoiceContent} 
 and invoice_title = #{invoiceTitle} 
 and shipping_address = #{shippingAddress} 
 and delete_flag = #{deleteFlag} 
 and create_time = #{createTime} 
 and pay_time = #{payTime} 
 and end_time = #{endTime} 



        order by #{sort} #{order}


        order by id desc



    limit #{offset}, #{limit}

