SELECT * FROM f01_shop WHERE delete_flag = 1 and status = 1
AND type = #{shopQueryForm.type}
AND examine_status = #{shopQueryForm.examineStatus}
AND name LIKE CONCAT('%',#{shopQueryForm.name},'%')
ORDER BY id DESC

