mapper.select:
select
    *
from f01_shop
where
CONVERT(longitude,DECIMAL(27,17)) >= #{minlng} and CONVERT(longitude,DECIMAL(27,17)) <= #{maxlng}
and CONVERT(latitude,DECIMAL(27,17)) >= #{minlat} and CONVERT(latitude,DECIMAL(27,17)) <= #{maxlat}

SELECT
    *,
    ROUND(
        6378.138 * 2 * ASIN(
            SQRT(
                POW(
                    SIN(
                        (
                            #{latitude} * PI() / 180 - latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS(#{latitude} * PI() / 180) * COS(latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{longitude} * PI() / 180 - longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
            )
        ) * 1000
    ) AS distance
FROM
    f01_shop
    where examine_status = 2
having distance <= 1000000 and main_project=3
ORDER BY
    distance ASC
